import React from "react"

const Layout: React.FC = ({ children }) => {
  return (
    <div>
      <div className="globalContainer">{children}</div>
      <style jsx global>{`
        html {
          font-size: 62.5%;
        }
        body {
          margin: 0;
          padding: 0;
          font-size: 1.6rem;
          font-weight: 400;
          line-height: 1.8;
          color: #333;
          font-family: "PT Sans", sans-serif;
        }
        @media screen and (min-width: 768px) {
          .globalContainer {
            margin: 4rem auto;
            width: 100%;
          }
        }
        .globalContainer {
          margin: 4rem 2rem;
          max-width: 1200px;
        }
        h1 {
          font-weight: 700;
        }
        p {
          margin-bottom: 10px;
        }
      `}</style>
    </div>
  )
}

export default Layout
