import { useVisibilityContext } from "../context/VisibilityContext"
import css from "styled-jsx/css"

const Menu = () => {
  const { toggleVisibility } = useVisibilityContext()
  return (
    <div className="menuContainer">
      <div className="arrow">X</div>
      <div className="innerMenuContainer">
        <h1>Coucou</h1>
      </div>
      <style jsx>{menuStyles}</style>
    </div>
  )
}

const menuStyles = css`
  .menuContainer {
  }
  .arrow {
    position: absolute;
    top: 0;
    right: 2rem;
    font-size: 10rem;
  }
  .innerMenuContainer {
    position: absolute;
    top: 0;
    right: 0;
    left: 0;
    bottom: 0;
    height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
  }
`

export default Menu
