import React from "react"

const Layout: React.FC = ({ children }) => {
  return (
    <div>
      {children}
      <style jsx global>{`
        html {
          font-size: 62.5%;
        }
        body {
          margin: 0;
          padding: 0;
          font-size: 1.6rem;
          font-weight: 400;
          line-height: 1.8;
          color: #333;
          font-family: "PT Sans", sans-serif;
        }
        h1 {
          font-weight: 700;
        }
        p {
          margin-bottom: 10px;
        }
      `}</style>
    </div>
  )
}

export default Layout
