import { useContext } from "react"
import VisibilityContext from "../../context/VisibilityContext"

interface PropositioProps {
  index: string
  pars: string
  txt: string
}

const Propositio: React.FC<PropositioProps> = ({ index, pars, txt }) => {
  const { showPropositio } = useContext(VisibilityContext)
  return (
    showPropositio && (
      <div id={`pars${pars}-propositio${index}`}>
        <h3>Proposition {index}</h3>
        <p>{txt}</p>
      </div>
    )
  )
}

export default Propositio
