  const sanitizer = DOMPurify.sanitize
  console.log({ sanitizer })
  return (
    <div>
      <p dangerouslySetInnerHTML={{ __html: txt }}></p>
      <style jsx>{`
        p {
          font-size: 16px;
          font-weight: 300;
        }
        br {
          padding: 16px 0;
          content: "";
        }
      `}</style>
    </div>
  )
}

export default Praefatio
