import React, { useState } from "react"
import {
  useVisibilityContext,
  VisibilityContextState,
} from "../context/VisibilityContext"
import css from "styled-jsx/css"
import Tick from "./IconTick"
import Circle from "./IconCircle"
import { colors } from "../styling/ds"
import MenuItemVisibility from "./MenuItemVisibility"

const Menu = () => {
  const {
    toggleVisibility,
    showAppendix,
    showAxioma,
    showCaput,
    showCorollarium,
    showDemonstratio,
    showLemma,
    showPropositio,
    showPostulatum,
    showPraefatio,
    showScholium,
  } = useVisibilityContext()

  const [showMenu, toggleMenu] = useState<boolean>(false)

  const handleClickOnToggleVisibility = (
    field: keyof Omit<VisibilityContextState, "toggleVisiblity">
  ) => (event) => {
    event.stopPropagation()
    toggleVisibility(field)
  }

  return (
    <div className="menuContainer" onClick={() => toggleMenu((prev) => !prev)}>
      <div className="arrow">{showMenu ? "-" : "X"}</div>
      {showMenu && (
        <div className="innerMenuContainer">
          <ul>
            <MenuItemVisibility
              onClick={handleClickOnToggleVisibility("showAppendix")}
              selected={showAppendix}
              label="Appendix"
            />
            <MenuItemVisibility
              onClick={handleClickOnToggleVisibility("showAxioma")}
              selected={showAxioma}
              label="Axioma"
            />
            <MenuItemVisibility
              onClick={handleClickOnToggleVisibility("showCaput")}
              selected={showCaput}
              label="Caput"
            />
            <li onClick={handleClickOnToggleVisibility("showCaput")}>
              {showCaput ? <Tick /> : <Circle />}&nbsp;Caput
            </li>
            <li onClick={handleClickOnToggleVisibility("showCorollarium")}>
              {showCorollarium ? <Tick /> : <Circle />}&nbsp;Corollarium
            </li>
            <li onClick={handleClickOnToggleVisibility("showDemonstratio")}>
              {showDemonstratio ? <Tick /> : <Circle />}&nbsp;Demonstratio
            </li>
            <li onClick={handleClickOnToggleVisibility("showLemma")}>
              {showLemma ? <Tick /> : <Circle />}&nbsp;Lemma
            </li>
            <li onClick={handleClickOnToggleVisibility("showPropositio")}>
              {showPropositio ? <Tick /> : <Circle />}&nbsp;Propositio
            </li>
            <li onClick={handleClickOnToggleVisibility("showPostulatum")}>
              {showPostulatum ? <Tick /> : <Circle />}&nbsp;Postulatum
            </li>
            <li onClick={handleClickOnToggleVisibility("showPraefatio")}>
              {showPraefatio ? <Tick /> : <Circle />}&nbsp;Praefatio
            </li>
            <li onClick={handleClickOnToggleVisibility("showScholium")}>
              {showScholium ? <Tick /> : <Circle />}&nbsp;Scholium
            </li>
          </ul>
        </div>
      )}
      <style jsx>{menuStyles}</style>
    </div>
  )
}

const menuStyles = css`
  .menuContainer {
  }
  .arrow {
    position: fixed;
    top: 0;
    right: 4rem;
    font-size: 10rem;
    z-index: 2;
  }
  .innerMenuContainer {
    background-color: royalblue;
    opacity: 0.8;
    position: fixed;
    top: 0;
    right: 0;
    left: 0;
    bottom: 0;
    height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    overflow-y: hidden;
  }
  h2 {
    font-size: 10rem;
  }
  ul {
    list-style: none;
  }
  li {
    font-size: 6rem;
  }
  li svg {
    border: 1px solid hotpink;
  }
`

export default Menu
