import React, { useState } from "react"
import { useVisibilityContext } from "../context/VisibilityContext"
import css from "styled-jsx/css"

const Menu = () => {
  const {
    toggleVisibility,
    showAppendix,
    showAxioma,
    showCaput,
    showCorollarium,
    showDemonstratio,
    showLemma,
    showPropositio,
    showPostulatum,
    showPraefatio,
    showScholium,
  } = useVisibilityContext()
  const [showMenu, toggleMenu] = useState<boolean>(false)

  return (
    <div className="menuContainer">
      <div className="arrow" onClick={() => toggleMenu((prev) => !prev)}>
        {showMenu ? "-" : "X"}
      </div>
      {showMenu && (
        <div className="innerMenuContainer">
          <ul>
            <li onClick={() => toggleVisibility("showAppendix")}>Appendix</li>
            <li onClick={() => toggleVisibility("showAxioma")}>Axioma</li>
            <li onClick={() => toggleVisibility("showCaput")}>Caput</li>
            <li onClick={() => toggleVisibility("showCorollarium")}>
              Corollarium
            </li>
            <li onClick={() => toggleVisibility("showDemonstratio")}>
              Demonstratio
            </li>
            <li onClick={() => toggleVisibility("showLemma")}>Lemma</li>
            <li onClick={() => toggleVisibility("showPropositio")}>
              Propositio
            </li>
            <li onClick={() => toggleVisibility("showPostulatum")}>
              Postulatum
            </li>
            <li onClick={() => toggleVisibility("showPraefatio")}>Praefatio</li>
            <li onClick={() => toggleVisibility("showScholium")}>Scholium</li>
          </ul>
        </div>
      )}
      <style jsx>{menuStyles}</style>
    </div>
  )
}

const menuStyles = css`
  .menuContainer {
  }
  .arrow {
    position: fixed;
    top: 0;
    right: 4rem;
    font-size: 10rem;
    z-index: 2;
  }
  .innerMenuContainer {
    background-color: royalblue;
    opacity: 0.8;
    position: fixed;
    top: 0;
    right: 0;
    left: 0;
    bottom: 0;
    height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    overflow-y: hidden;
  }
  h2 {
    font-size: 10rem;
  }
`

export default Menu
