import React from "react"

const Layout: React.FC = ({ children }) => {
  return (
    <div>
      <div className="globalContainer">{children}</div>
      <style jsx global>{`
        html {
          font-size: 62.5%;
        }
        body {
          color: #333;
          font-family: "PT Sans", sans-serif;
          font-size: 1.6rem;
          font-weight: 400;
          line-height: 1.8;
          margin: 0;
          padding: 0;
        }
        @media screen and (min-width: 768px) {
          .globalContainer {
            margin: 4rem auto;
            width: 100%;
          }
        }
        .globalContainer {
          margin: 4rem 2rem;
          max-width: 1200px;
        }
        h1 {
          font-weight: 800;
          font-size: 2.4rem;
        }
        h2 {
          font-weight: 600;
          font-size: 2rem;
        }
        h2 {
          font-weight: 600;
          font-size: 2rem;
        }
        p {
          margin-bottom: 10px;
        }
      `}</style>
    </div>
  )
}

export default Layout
